import styled from 'styled-components';

const Container = styled.div`
    display:flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    gap: 3rem;
    background-color: #131324;
    height: 100vh;
    width: 100vw;
    .loader{
        max-inline-size: 100%;
    }
    .title-container{
        h1{
            color: white;
        }
    }
    .avatars{
        display:flex;
        gap: 2rem;
        .avatar{
            border: 0.4rem solid transparent;
        }
    }
`;


export default Container;
