import styled from 'styled-components';

const WelcomeContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    color: white;
    img {
        height: 15rem;
    }
    h1{
        font-weight: 600;
        font-size: 3rem;
    }
    span {
        color: rgb(247,148,29);
        font-weight: 500;
    }
`;


export default WelcomeContainer;
